        });
      }

      // If no session and no appointment keyword, provide general info
      if (!session) {
        return res.json({
          success: true,
          message: `Hello! I'm Dr. ${doctor.name}'s appointment booking assistant.\n\nüè• Clinic: ${doctor.clinicName || 'Homeo Health Clinic'}\nüìç Location: ${doctor.clinicLocation || 'Kolkata'}\n\nTo book an appointment, please type "appointment" or "booking".`
        });
      }

      // Handle session-based conversation
      const sessionData = session.sessionData || {};
      let responseMessage = '';
      let nextStep = session.step;

      switch (session.step) {
        case 'awaiting_name':
          sessionData.name = message.trim();
          nextStep = 'awaiting_age';
          responseMessage = `Thank you, ${sessionData.name}!\n\nüéÇ Please tell me your age:`;
          break;

        case 'awaiting_age':
          const age = parseInt(message.trim());
          if (isNaN(age) || age < 1 || age > 150) {
            responseMessage = `Please provide a valid age (1-150):\n\nExample: 25`;
            nextStep = session.step; // Stay in same step
          } else {
            sessionData.age = age;
            nextStep = 'awaiting_gender';
            responseMessage = `Age: ${age} years\n\n‚ößÔ∏è Please tell me your gender:\n\n‚Ä¢ Type "Male" or "M"\n‚Ä¢ Type "Female" or "F"\n‚Ä¢ Type "Other" or "O"`;
          }
          break;

        case 'awaiting_gender':
          const genderInput = message.trim().toLowerCase();
          let gender = '';
          if (['male', 'm', '‡¶™‡ßÅ‡¶∞‡ßÅ‡¶∑'].includes(genderInput)) {
            gender = 'Male';
          } else if (['female', 'f', '‡¶Æ‡¶π‡¶ø‡¶≤‡¶æ'].includes(genderInput)) {
            gender = 'Female';
          } else if (['other', 'o', '‡¶Ö‡¶®‡ßç‡¶Ø‡¶æ‡¶®‡ßç‡¶Ø'].includes(genderInput)) {
            gender = 'Other';
          } else {
            responseMessage = `Please choose a valid gender:\n\n‚Ä¢ Type "Male" or "M"\n‚Ä¢ Type "Female" or "F"\n‚Ä¢ Type "Other" or "O"`;
            nextStep = session.step; // Stay in same step
            break;
          }
          sessionData.gender = gender;
          nextStep = 'awaiting_location';
          responseMessage = `Gender: ${gender}\n\nüìç Please tell me your location/area:\n\nExample: Kolkata, Dhaka, etc.`;
          break;

        case 'awaiting_location':
          sessionData.location = message.trim();
          nextStep = 'completed';

          // Now process the booking with collected data
          try {
            // Check doctor availability
            const availability = await storage.getDoctorAvailability(doctor.id);
            if (!availability.length) {
              responseMessage = `Sorry, Dr. ${doctor.name} currently has no available appointment slots. Please try again later.`;
              break;
            }

            // Find next available slot
            const nextSlot = await storage.findNextAvailableSlot(doctor.id);
            if (!nextSlot) {
              responseMessage = `Sorry, no available appointment slots in the next 7 days. Please try again later.`;
              break;
            }

            // Create or find existing patient
            let patient = await storage.getPatientByPhoneAndDoctor(patientPhone, doctor.id);

            if (!patient) {
              patient = await storage.createPatient({
                name: sessionData.name,
                age: sessionData.age,
                gender: sessionData.gender,
                phone: patientPhone,
                location: sessionData.location,
                doctorId: doctor.id
              });
            }

            // Create appointment
            const appointment = await storage.createAppointment({
              patientId: patient.id,
              doctorId: doctor.id,
              dateTime: nextSlot,
              type: 'consultation',
              status: 'upcoming',
              notes: `Booked via WhatsApp conversation`
            });

            // Format confirmation
            const appointmentDate = new Date(nextSlot).toLocaleDateString('en-BD');
            const appointmentTime = new Date(nextSlot).toLocaleTimeString('en-BD', { 
              hour: '2-digit', 
              minute: '2-digit',
              hour12: true 
            });

