          </Route>

          {isDoctor && (
            <Route path="/calendar">
              <CalendarPage user={user} />
            </Route>
          )}

          {(isAdmin || isDoctor) && (
            <>
              <Route path="/patients">
                <PatientsPage user={user} onNavigate={handleNavigate} />
              </Route>
              <Route path="/patients/:id">
                <PatientDetailPage />
              </Route>
              <Route path="/appointments">
                <AppointmentsPage user={user} onNavigate={handleNavigate} />
              </Route>
              <Route path="/prescriptions">
                <PrescriptionsPage user={user} onNavigate={handleNavigate} />
              </Route>
              <Route path="/medicines">
                <MedicinesPage user={user} onNavigate={handleNavigate} />
              </Route>
            </>
          )}

          {isDoctor && (
            <Route path="/doctor/settings">
              <DoctorSettingsPage user={user} onNavigate={handleNavigate} />
            </Route>
          )}

          {isAdmin && (
            <>
              <Route path="/admin">
                <AdminDashboard />
              </Route>
              <Route path="/admin/settings">
                <AdminSettingsPage user={user} onNavigate={handleNavigate} />
              </Route>
            </>
          )}

          <Route path="/notifications">
            <NotificationsPage />
          </Route>

          {/* Default route */}
          <Route path="/">
            {isAdmin ? (
              <AdminDashboard />
            ) : (
              <DashboardPage user={user} onNavigate={handleNavigate} />
            )}
          </Route>

          {/* 404 */}
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </main>

      {/* Bottom Navigation */}
      <BottomNavigation
        currentPage={currentPage}
        onNavigate={handleNavigate}
        userRole={user.role}
      />

      {/* Floating Action Button */}
      {(isAdmin || isDoctor) && (
        <button
          className="mobile-fab bg-primary text-white fab"
          onClick={() => setShowQuickActions(true)}
        >
          <Plus className="h-6 w-6" />
        </button>
      )}

      {/* Quick Actions Modal */}
      <QuickActionsModal
        isOpen={showQuickActions}
        onClose={() => setShowQuickActions(false)}
        onAction={handleQuickAction}
        userRole={user.role}
      />

      {/* Loading Overlay for mutations */}
      <LoadingOverlay 
        isVisible={logoutMutation.isPending} 
        message="Signing out..." 
      />
    </div>
  );
}

function App() {
  return (
    <LanguageProvider>
      <QueryClientProvider client={queryClient}>
        <AppContent />
        <Toaster />
      </QueryClientProvider>
    </LanguageProvider>
  );
}

export default App;

