  --mobile-neutral-200: hsl(214, 32%, 91%);
  --mobile-neutral-300: hsl(213, 27%, 84%);
  --mobile-neutral-400: hsl(215, 20%, 65%);
  --mobile-neutral-500: hsl(215, 16%, 47%);
  --mobile-neutral-600: hsl(215, 19%, 35%);
  --mobile-neutral-700: hsl(215, 25%, 27%);
  --mobile-neutral-800: hsl(217, 33%, 17%);
  --mobile-neutral-900: hsl(222, 84%, 5%);
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
