          </TabsContent>
        </Tabs>

        {/* Admin Management Modal */}
        <Dialog open={showAddAdminModal} onOpenChange={setShowAddAdminModal}>
          <DialogContent className="sm:max-w-[425px]">
            <DialogHeader>
              <DialogTitle>
                {editingAdmin ? 'Edit Admin User' : 'Add New Admin User'}
              </DialogTitle>
              <DialogDescription>
                {editingAdmin 
                  ? 'Update admin user information. Leave password blank to keep current password.'
                  : 'Create a new admin user with access to system management.'
                }
              </DialogDescription>
            </DialogHeader>
            <div className="py-4">
              <Form {...adminForm}>
                <form onSubmit={adminForm.handleSubmit(onSubmitAdmin)} className="space-y-4">
                  <FormField
                    control={adminForm.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Full Name</FormLabel>
                        <FormControl>
                          <Input placeholder="John Doe" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={adminForm.control}
                    name="username"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Username</FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="johndoe" 
                            {...field} 
                            disabled={editingAdmin?.username === 'admin'}
                          />
                        </FormControl>
                        <FormMessage />
                        {editingAdmin?.username === 'admin' && (
                          <p className="text-xs text-muted-foreground">
                            Main admin username cannot be changed
                          </p>
                        )}
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={adminForm.control}
                    name="password"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>
                          Password {editingAdmin && '(leave blank to keep current)'}
                        </FormLabel>
                        <FormControl>
                          <Input 
                            type="password" 
                            placeholder={editingAdmin ? "Leave blank to keep current" : "Enter password"} 
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={adminForm.control}
                    name="email"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email (Optional)</FormLabel>
                        <FormControl>
                          <Input type="email" placeholder="john@example.com" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <div className="flex justify-end space-x-2 pt-4">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => setShowAddAdminModal(false)}
                    >
                      Cancel
                    </Button>
                    <Button
                      type="submit"
                      disabled={addAdminMutation.isPending || updateAdminMutation.isPending}
                    >
                      {editingAdmin ? 'Update Admin' : 'Add Admin'}
                    </Button>
                  </div>
                </form>
              </Form>
            </div>
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

