import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { BarChart3, Users, UserPlus, Pill, Calendar, FileText, Eye, Edit, Trash2, Settings } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AdminPageProps {
  user: any;
  onNavigate: (page: string) => void;
}

export default function AdminPage({ user, onNavigate }: AdminPageProps) {
  const [showAddDoctorModal, setShowAddDoctorModal] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState<any>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [newDoctor, setNewDoctor] = useState({
    username: "",
    password: "",
    name: "",
    email: "",
    phone: "",
    clinicName: "",
    clinicNameBengali: "",
    clinicLocation: "",
    clinicLocationBengali: "",
    degree: "",
    degreeBengali: "",
    specialist: "",
    specialistBengali: "",
    extraNotes: "",
    extraNotesBengali: "",
    role: "doctor",
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: stats, isLoading: isLoadingStats } = useQuery({
    queryKey: ["/api/stats/dashboard"],
  });

  const { data: doctors = [], isLoading: isLoadingDoctors } = useQuery({
    queryKey: ["/api/users/doctors"],
  });

  const { data: patients = [] } = useQuery({
    queryKey: ["/api/patients"],
  });

  const { data: appointments = [] } = useQuery({
    queryKey: ["/api/appointments"],
  });

  const { data: prescriptions = [] } = useQuery({
    queryKey: ["/api/prescriptions"],
  });

  const { data: medicines = [] } = useQuery({
    queryKey: ["/api/medicines"],
  });

  const createDoctorMutation = useMutation({
    mutationFn: async (doctorData: any) => {
      const response = await apiRequest("POST", "/api/users", doctorData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users/doctors"] });
      queryClient.invalidateQueries({ queryKey: ["/api/stats/dashboard"] });
      toast({
        title: "Success",
        description: isEditing ? "Doctor updated successfully" : "Doctor added successfully",
      });
      setShowAddDoctorModal(false);
      setIsEditing(false);
      resetForm();
    },
    onError: () => {
      toast({
        title: "Error",
        description: isEditing ? "Failed to update doctor" : "Failed to add doctor",
        variant: "destructive",
      });
    },
  });

  const updateDoctorMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      const response = await apiRequest("PUT", `/api/users/${id}`, updates);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users/doctors"] });
      toast({
        title: "Success",
        description: "Doctor updated successfully",
      });
      setSelectedDoctor(null);
      setIsEditing(false);
      setShowAddDoctorModal(false);
      resetForm();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update doctor",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setNewDoctor({
      username: "",
      password: "",
      name: "",
      email: "",
      phone: "",
      clinicName: "",
      clinicNameBengali: "",
      clinicLocation: "",
      clinicLocationBengali: "",
      degree: "",
      degreeBengali: "",
      specialist: "",
      specialistBengali: "",
      extraNotes: "",
      extraNotesBengali: "",
      role: "doctor",
    });
  };

  const handleAddDoctor = () => {
    if (!newDoctor.username.trim() || !newDoctor.password.trim() || !newDoctor.name.trim()) {
      toast({
        title: "Error",
        description: "Please fill in username, password, and name",
        variant: "destructive",
      });
      return;
    }

    if (isEditing && selectedDoctor) {
      updateDoctorMutation.mutate({
        id: selectedDoctor.id,
        updates: newDoctor
      });
    } else {
      createDoctorMutation.mutate(newDoctor);
    }
  };

  const handleEditDoctor = (doctor: any) => {
    setSelectedDoctor(doctor);
    setNewDoctor({
      username: doctor.username || "",
      password: "", // Don't pre-fill password for security
      name: doctor.name || "",
      email: doctor.email || "",
      phone: doctor.phone || "",
      clinicName: doctor.clinicName || "",
      clinicNameBengali: doctor.clinicNameBengali || "",
      clinicLocation: doctor.clinicLocation || "",
      clinicLocationBengali: doctor.clinicLocationBengali || "",
      degree: doctor.degree || "",
      degreeBengali: doctor.degreeBengali || "",
      specialist: doctor.specialist || "",
      specialistBengali: doctor.specialistBengali || "",
      extraNotes: doctor.extraNotes || "",
      extraNotesBengali: doctor.extraNotesBengali || "",
      role: "doctor",
    });
    setIsEditing(true);
    setShowAddDoctorModal(true);
  };

  const handleDeleteDoctor = (doctor: any) => {
    // In a real implementation, you'd show a confirmation dialog
    // and implement a soft delete or deactivation
    updateDoctorMutation.mutate({
      id: doctor.id,
      updates: { isActive: false }
    });
  };

  // Get doctor-specific statistics
  const getDoctorStats = (doctorId: string) => {
    const doctorPatients = patients.filter((p: any) => p.doctorId === doctorId);
    const doctorAppointments = appointments.filter((a: any) => a.doctorId === doctorId);
    const doctorPrescriptions = prescriptions.filter((p: any) => p.doctorId === doctorId);

    return {
      patients: doctorPatients.length,
      appointments: doctorAppointments.length,
      prescriptions: doctorPrescriptions.length,
    };
  };

  const dashboardStats = [
    {
      title: "Total Doctors",
      value: stats?.totalDoctors || 0,
      icon: Users,
      color: "bg-primary bg-opacity-10 text-primary",
    },
    {
      title: "Total Patients",
      value: stats?.totalPatients || 0,
      icon: Users,
      color: "bg-secondary bg-opacity-10 text-secondary",
    },
    {
      title: "Total Appointments",
      value: appointments.length,
      icon: Calendar,
      color: "bg-accent bg-opacity-10 text-accent",
    },
    {
      title: "Total Medicines",
      value: stats?.totalMedicines || 0,
      icon: Pill,
      color: "bg-orange-500 bg-opacity-10 text-orange-500",
    },
  ];

  if (isLoadingStats || isLoadingDoctors) {
    return (
      <div className="p-4 space-y-4 pb-20">
        <div className="animate-pulse space-y-4">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="bg-white rounded-xl p-4 shadow-sm border h-24"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="pb-20">
      {/* Header */}
      <div className="p-4 border-b bg-white sticky top-16 z-10">
        <div className="flex items-center justify-between">
          <h1 className="text-xl font-semibold text-neutral-800">Admin Panel</h1>
          <Button
            size="sm"
            variant="outline"
            onClick={() => onNavigate('/admin/settings')}
            className="touch-target"
          >
            <Settings className="h-4 w-4 mr-2" />
            Settings
          </Button>
        </div>
      </div>

      <div className="p-4">
        <Tabs defaultValue="dashboard" className="space-y-4">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="dashboard">Dashboard</TabsTrigger>
            <TabsTrigger value="doctors">Doctors</TabsTrigger>
            <TabsTrigger value="system">System</TabsTrigger>
          </TabsList>

          {/* Dashboard Tab */}
          <TabsContent value="dashboard" className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              {dashboardStats.map((stat, index) => {
                const Icon = stat.icon;

                return (
                  <Card key={index} className="shadow-sm border border-neutral-200">
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-3">
                        <div className={`w-10 h-10 rounded-lg flex items-center justify-center ${stat.color}`}>
                          <Icon size={20} />
                        </div>
                        <div>
                          <p className="text-2xl font-bold text-neutral-800">{stat.value}</p>
                          <p className="text-sm text-neutral-500">{stat.title}</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>

            {/* Recent Activity */}
            <Card className="shadow-sm border border-neutral-200">
              <CardHeader>
                <CardTitle className="text-lg">System Overview</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-neutral-600">Active Doctors</span>
                  <span className="font-semibold">{doctors.filter((d: any) => d.isActive).length}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-neutral-600">Total Prescriptions</span>
                  <span className="font-semibold">{prescriptions.length}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-neutral-600">Available Medicines</span>
                  <span className="font-semibold">{medicines.length}</span>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Doctors Tab */}
          <TabsContent value="doctors" className="space-y-4">
            <div className="flex justify-between items-center">
              <h2 className="text-lg font-semibold text-neutral-800">Doctor Management</h2>
              <Button
                size="sm"
                className="touch-target"
                onClick={() => {
                  setIsEditing(false);
                  resetForm();
                  setShowAddDoctorModal(true);
                }}
              >
                <UserPlus className="h-4 w-4 mr-2" />
                Add Doctor
              </Button>
            </div>

            <div className="space-y-3">
              {doctors.length === 0 ? (
                <Card className="shadow-sm border border-neutral-200">
                  <CardContent className="p-8 text-center">
                    <Users className="h-16 w-16 text-neutral-300 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-neutral-800 mb-2">No doctors registered</h3>
                    <p className="text-neutral-500 mb-4">Add your first doctor to get started</p>
                    <Button onClick={() => setShowAddDoctorModal(true)}>
                      <UserPlus className="h-4 w-4 mr-2" />
                      Add Doctor
                    </Button>
                  </CardContent>
                </Card>
              ) : (
                doctors.map((doctor: any) => {
                  const doctorStats = getDoctorStats(doctor.id);

                  return (
                    <Card key={doctor.id} className="shadow-sm border border-neutral-200 card-hover">
                      <CardContent className="p-4">
                        <div className="flex items-center justify-between mb-3">
                          <div className="flex items-center space-x-3">
                            <Avatar className="w-12 h-12">
                              <AvatarFallback className="bg-gradient-to-br from-primary to-secondary text-white">
                                {doctor.name.split(' ').map((n: string) => n[0]).join('').toUpperCase()}
                              </AvatarFallback>
                            </Avatar>
                            <div>
                              <h3 className="font-semibold text-neutral-800">{doctor.name}</h3>
                              <p className="text-sm text-neutral-500">@{doctor.username}</p>
                              {doctor.specialist && (
                                <p className="text-xs text-neutral-400">{doctor.specialist}</p>
                              )}
                            </div>
                          </div>
                          <div className="flex items-center space-x-2">
                            <Button
                              size="sm"
                              variant="ghost"
                              onClick={() => handleEditDoctor(doctor)}
                              className="p-2"
                            >
                              <Edit className="h-4 w-4" />
                            </Button>
                            <Button
                              size="sm"
                              variant="ghost"
                              onClick={() => handleDeleteDoctor(doctor)}
                              className="p-2 text-red-600 hover:text-red-700"
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>

                        <div className="grid grid-cols-3 gap-4 text-sm">
                          <div className="text-center">
                            <p className="font-semibold text-neutral-800">{doctorStats.patients}</p>
                            <p className="text-neutral-500">Patients</p>
                          </div>
                          <div className="text-center">
                            <p className="font-semibold text-neutral-800">{doctorStats.appointments}</p>
                            <p className="text-neutral-500">Appointments</p>
                          </div>
                          <div className="text-center">
                            <p className="font-semibold text-neutral-800">{doctorStats.prescriptions}</p>
                            <p className="text-neutral-500">Prescriptions</p>
                          </div>
                        </div>

                        <div className="flex space-x-2 mt-3">
                          <Button
                            size="sm"
                            variant="outline"
                            className="flex-1 touch-target"
                            onClick={() => setSelectedDoctor(doctor)}
                          >
                            <Eye className="h-4 w-4 mr-1" />
                            View Details
                          </Button>
                        </div>
                      </CardContent>
                    </Card>
                  );
                })
              )}
            </div>
          </TabsContent>

          {/* System Tab */}
          <TabsContent value="system" className="space-y-4">
            <Card className="shadow-sm border border-neutral-200">
              <CardHeader>
                <CardTitle className="text-lg">System Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="text-sm space-y-2">
                  <div className="flex justify-between">
                    <span className="text-neutral-600">Application Version:</span>
                    <span className="font-medium">1.0.0</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-neutral-600">Database Status:</span>
                    <span className="text-green-600 font-medium">Connected</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-neutral-600">Last Backup:</span>
                    <span className="font-medium">Never</span>
                  </div>
                </div>

                <div className="pt-4 border-t">
                  <Button variant="outline" className="w-full touch-target mb-2">
                    Export Data
                  </Button>
                  <Button variant="outline" className="w-full touch-target">
                    System Logs
                  </Button>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>

      {/* Add/Edit Doctor Modal */}
      <Dialog open={showAddDoctorModal} onOpenChange={setShowAddDoctorModal}>
        <DialogContent className="max-w-lg max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>{isEditing ? 'Edit Doctor' : 'Add New Doctor'}</DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="username">Username *</Label>
                <Input
                  id="username"
                  value={newDoctor.username}
                  onChange={(e) => setNewDoctor({ ...newDoctor, username: e.target.value })}
                  placeholder="Username for login"
                  className="form-input"
                />
              </div>
              <div>
                <Label htmlFor="password">Password *</Label>
                <Input
                  id="password"
                  type="password"
                  value={newDoctor.password}
                  onChange={(e) => setNewDoctor({ ...newDoctor, password: e.target.value })}
                  placeholder={isEditing ? "Leave blank to keep current" : "Password"}
                  className="form-input"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="name">Full Name *</Label>
              <Input
                id="name"
                value={newDoctor.name}
                onChange={(e) => setNewDoctor({ ...newDoctor, name: e.target.value })}
                placeholder="Dr. Full Name"
                className="form-input"
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  type="email"
                  value={newDoctor.email}
                  onChange={(e) => setNewDoctor({ ...newDoctor, email: e.target.value })}
                  placeholder="email@example.com"
                  className="form-input"
                />
              </div>
              <div>
                <Label htmlFor="phone">Phone</Label>
                <Input
                  id="phone"
                  type="tel"
                  value={newDoctor.phone}
                  onChange={(e) => setNewDoctor({ ...newDoctor, phone: e.target.value })}
                  placeholder="Phone number"
                  className="form-input"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="clinicName">Clinic Name</Label>
              <Input
                id="clinicName"
                value={newDoctor.clinicName}
                onChange={(e) => setNewDoctor({ ...newDoctor, clinicName: e.target.value })}
                placeholder="Clinic name in English"
                className="form-input"
              />
            </div>

            <div>
              <Label htmlFor="clinicNameBengali">Clinic Name (Bengali)</Label>
              <Input
                id="clinicNameBengali"
                value={newDoctor.clinicNameBengali}
                onChange={(e) => setNewDoctor({ ...newDoctor, clinicNameBengali: e.target.value })}
                placeholder="‡¶ï‡ßç‡¶≤‡¶ø‡¶®‡¶ø‡¶ï‡ßá‡¶∞ ‡¶®‡¶æ‡¶Æ ‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ‡¶Ø‡¶º"
                className="form-input"
              />
            </div>

            <div>
              <Label htmlFor="clinicLocation">Location</Label>
              <Input
                id="clinicLocation"
                value={newDoctor.clinicLocation}
                onChange={(e) => setNewDoctor({ ...newDoctor, clinicLocation: e.target.value })}
                placeholder="Clinic location in English"
                className="form-input"
              />
            </div>

            <div>
              <Label htmlFor="degree">Degree</Label>
              <Input
                id="degree"
                value={newDoctor.degree}
                onChange={(e) => setNewDoctor({ ...newDoctor, degree: e.target.value })}
                placeholder="e.g., BHMS, MD (Hom)"
                className="form-input"
              />
            </div>

            <div>
              <Label htmlFor="specialist">Specialization</Label>
              <Input
                id="specialist"
                value={newDoctor.specialist}
                onChange={(e) => setNewDoctor({ ...newDoctor, specialist: e.target.value })}
                placeholder="Area of specialization"
                className="form-input"
              />
            </div>

            <div className="flex space-x-3 pt-4">
              <Button
                variant="outline"
                className="flex-1"
                onClick={() => {
                  setShowAddDoctorModal(false);
                  setIsEditing(false);
                  resetForm();
                }}
              >
                Cancel
              </Button>
              <Button
                className="flex-1"
                onClick={handleAddDoctor}
                disabled={createDoctorMutation.isPending || updateDoctorMutation.isPending}
              >
                {createDoctorMutation.isPending || updateDoctorMutation.isPending 
                  ? (isEditing ? "Updating..." : "Adding...") 
                  : (isEditing ? "Update Doctor" : "Add Doctor")
                }
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Doctor Details Modal */}
      <Dialog open={!!selectedDoctor && !isEditing} onOpenChange={() => setSelectedDoctor(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Doctor Details</DialogTitle>
          </DialogHeader>

          {selectedDoctor && (
            <div className="space-y-4">
              <div className="flex items-center space-x-4">
                <Avatar className="w-16 h-16">
                  <AvatarFallback className="bg-gradient-to-br from-primary to-secondary text-white text-lg">
                    {selectedDoctor.name.split(' ').map((n: string) => n[0]).join('').toUpperCase()}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <h3 className="text-lg font-semibold">{selectedDoctor.name}</h3>
                  <p className="text-neutral-600">@{selectedDoctor.username}</p>
                  {selectedDoctor.degree && (
                    <p className="text-sm text-neutral-500">{selectedDoctor.degree}</p>
                  )}
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4 text-sm">
                {selectedDoctor.email && (
                  <div>
                    <span className="font-medium text-neutral-700">Email:</span>
                    <p>{selectedDoctor.email}</p>
                  </div>
                )}
                {selectedDoctor.phone && (
                  <div>
                    <span className="font-medium text-neutral-700">Phone:</span>
                    <p>{selectedDoctor.phone}</p>
                  </div>
                )}
                {selectedDoctor.clinicName && (
                  <div className="col-span-2">
                    <span className="font-medium text-neutral-700">Clinic:</span>
                    <p>{selectedDoctor.clinicName}</p>
                    {selectedDoctor.clinicLocation && (
                      <p className="text-neutral-500">{selectedDoctor.clinicLocation}</p>
                    )}
                  </div>
                )}
              </div>

              {selectedDoctor.specialist && (
                <div>
                  <span className="font-medium text-neutral-700">Specialization:</span>
                  <p className="text-sm mt-1">{selectedDoctor.specialist}</p>
                </div>
              )}

              <div className="flex space-x-3 pt-4">
                <Button
                  variant="outline"
                  className="flex-1"
                  onClick={() => setSelectedDoctor(null)}
                >
                  Close
                </Button>
                <Button
                  className="flex-1"
                  onClick={() => handleEditDoctor(selectedDoctor)}
                >
                  <Edit className="h-4 w-4 mr-2" />
                  Edit Doctor
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/AdminSettingsPage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Settings, User, Shield } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AdminSettingsPageProps {
  user: any;
  onNavigate: (page: string) => void;
}

export default function AdminSettingsPage({ user, onNavigate }: AdminSettingsPageProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: doctors = [], isLoading } = useQuery({
    queryKey: ["/api/users/doctors"],
    queryFn: async () => {
      const response = await fetch("/api/users?role=doctor");
      if (!response.ok) throw new Error("Failed to fetch doctors");
      return response.json();
    },
  });

  const updatePermissionMutation = useMutation({
    mutationFn: async ({ userId, canDeletePatients }: { userId: string; canDeletePatients: boolean }) => {
      const response = await apiRequest("PATCH", `/api/users/${userId}/permissions`, {
        canDeletePatients,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users/doctors"] });
      toast({
        title: "Success",
        description: "Doctor permissions updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update permissions",
        variant: "destructive",
      });
    },
  });

  const handlePermissionChange = (doctorId: string, canDeletePatients: boolean) => {
    updatePermissionMutation.mutate({ userId: doctorId, canDeletePatients });
  };

  if (user.role !== "admin") {
    return (
      <div className="p-4">
        <Card className="shadow-sm border border-neutral-200">
          <CardContent className="p-8 text-center">
            <Shield className="h-16 w-16 text-neutral-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-neutral-800 mb-2">Access Denied</h3>
            <p className="text-neutral-500">Admin privileges required to access this page.</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="p-4 space-y-4 pb-20">
        <div className="animate-pulse space-y-4">
          {[1, 2, 3].map((i) => (
            <div key={i} className="bg-white rounded-xl p-4 shadow-sm border h-24"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="pb-20">
      {/* Header */}
      <div className="p-4 border-b bg-white sticky top-16 z-10">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-xl font-semibold text-neutral-800">Admin Settings</h1>
          <Settings className="h-6 w-6 text-neutral-600" />
        </div>
      </div>

      {/* Doctor Permissions */}
      <div className="p-4 space-y-4">
        <Card className="shadow-sm border border-neutral-200">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <User className="h-5 w-5" />
              <span>Doctor Permissions</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {doctors.length === 0 ? (
              <div className="text-center py-8">
                <User className="h-12 w-12 text-neutral-300 mx-auto mb-4" />
                <p className="text-neutral-500">No doctors found</p>
              </div>
            ) : (
              doctors.map((doctor: any) => (
                <div key={doctor.id} className="flex items-center justify-between p-4 bg-neutral-50 rounded-lg">
                  <div className="flex items-center space-x-3">
                    <Avatar className="w-10 h-10">
                      <AvatarFallback className="bg-gradient-to-br from-primary to-secondary text-white">
                        {doctor.name.split(' ').map((n: string) => n[0]).join('').toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <h3 className="font-medium text-neutral-800">{doctor.name}</h3>
                      <p className="text-sm text-neutral-500">{doctor.email || doctor.username}</p>
                      {doctor.clinicName && (
                        <p className="text-xs text-neutral-400">{doctor.clinicName}</p>
                      )}
                    </div>
                  </div>

                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-2">
                      <Label htmlFor={`delete-permission-${doctor.id}`} className="text-sm font-medium">
                        Delete Patients
                      </Label>
                      <Switch
                        id={`delete-permission-${doctor.id}`}
                        checked={doctor.canDeletePatients || false}
                        onCheckedChange={(checked) => handlePermissionChange(doctor.id, checked)}
                        disabled={updatePermissionMutation.isPending}
                      />
                    </div>
                  </div>
                </div>
              ))
            )}
          </CardContent>
        </Card>

        <Card className="shadow-sm border border-neutral-200">
          <CardHeader>
            <CardTitle>Permission Guide</CardTitle>
          </CardHeader>
          <CardContent className="space-y-3 text-sm text-neutral-600">
            <div className="flex items-start space-x-2">
              <div className="w-2 h-2 bg-primary rounded-full mt-2 flex-shrink-0"></div>
              <div>
                <p className="font-medium">Delete Patients Permission</p>
                <p>Allows doctors to permanently delete patients from their patient list. This will also delete all associated prescriptions and appointments.</p>
              </div>
            </div>
            <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-3 mt-4">
              <p className="text-yellow-800 text-sm">
                <strong>Warning:</strong> Patient deletion is permanent and cannot be undone. Only grant this permission to trusted doctors who understand the implications.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/AppointmentsPage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Search, Plus, Calendar, Clock, CheckCircle, XCircle, Eye, Trash2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { format, parseISO } from "date-fns";
import { useLanguage } from "@/contexts/LanguageContext";

interface AppointmentsPageProps {
  user: any;
  onNavigate: (page: string) => void;
}

export default function AppointmentsPage({ user, onNavigate }: AppointmentsPageProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [selectedAppointment, setSelectedAppointment] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { t, translateData } = useLanguage();

  const { data: appointments = [], isLoading } = useQuery({
    queryKey: ["/api/appointments"],
  });

  const { data: patients = [] } = useQuery({
    queryKey: ["/api/patients"],
  });

  const updateAppointmentMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: any }) => {
      const response = await apiRequest("PUT", `/api/appointments/${id}`, updates);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/appointments"] });
      toast({
        title: "Success",
        description: "Appointment updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update appointment",
        variant: "destructive",
      });
    },
  });

  const deleteAppointmentMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/appointments/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/appointments"] });
      toast({
        title: "Success",
        description: "Appointment deleted successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete appointment",
        variant: "destructive",
      });
    },
  });

  // Enrich appointments with patient data
  const enrichedAppointments = appointments.map((appointment: any) => {
    const patient = patients.find((p: any) => p.id === appointment.patientId);
    return {
      ...appointment,
      patientName: patient?.name || 'Unknown Patient',
      patientPhone: patient?.phone || '',
      patientAge: patient?.age || '',
      patientGender: patient?.gender || '',
      patientId: patient?.patientId || 'N/A',
    };
  });

  // Filter appointments
  const filteredAppointments = enrichedAppointments.filter((appointment: any) => {
    const matchesSearch = 
      appointment.patientName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      appointment.patientId.toLowerCase().includes(searchTerm.toLowerCase()) ||
      appointment.appointmentId.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = statusFilter === "all" || appointment.status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  // Group appointments by status
  const upcomingAppointments = filteredAppointments.filter((apt: any) => apt.status === "upcoming");
  const completedAppointments = filteredAppointments.filter((apt: any) => apt.status === "completed");
  const cancelledAppointments = filteredAppointments.filter((apt: any) => apt.status === "cancelled");

  const handleStatusUpdate = (appointmentId: string, newStatus: string) => {
    updateAppointmentMutation.mutate({
      id: appointmentId,
      updates: { status: newStatus }
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming':
        return 'bg-orange-100 text-orange-600';
      case 'completed':
        return 'bg-green-100 text-green-600';
      case 'cancelled':
        return 'bg-red-100 text-red-600';
      default:
        return 'bg-neutral-100 text-neutral-600';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'upcoming':
        return <Clock className="h-4 w-4" />;
      case 'completed':
        return <CheckCircle className="h-4 w-4" />;
      case 'cancelled':
        return <XCircle className="h-4 w-4" />;
      default:
        return <Calendar className="h-4 w-4" />;
    }
  };

  const AppointmentCard = ({ appointment }: { appointment: any }) => (
    <Card className="shadow-sm border border-neutral-200 card-hover">
      <CardContent className="p-4">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-3">
            <Avatar className="w-12 h-12">
              <AvatarFallback className="bg-gradient-to-br from-primary to-secondary text-white">
                {appointment.patientName.split(' ').map((n: string) => n[0]).join('').toUpperCase()}
              </AvatarFallback>
            </Avatar>
            <div>
              <h3 className="font-semibold text-neutral-800">{appointment.patientName}</h3>
              <p className="text-sm text-neutral-500">
                {appointment.patientGender}, {appointment.patientAge} ‚Ä¢ {appointment.patientId}
              </p>
            </div>
          </div>
          <div className="text-right">
            <p className="text-sm font-medium text-neutral-700">
              {format(parseISO(appointment.dateTime), 'h:mm a')}
            </p>
            <p className="text-xs text-neutral-500">
              {format(parseISO(appointment.dateTime), 'MMM d, yyyy')}
            </p>
          </div>
        </div>

        <div className="flex items-center justify-between mb-3">
          <span className={`inline-flex items-center space-x-1 px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(appointment.status)}`}>
            {getStatusIcon(appointment.status)}
            <span className="capitalize">{appointment.status}</span>
          </span>
          <span className="text-xs text-neutral-500">ID: {appointment.appointmentId}</span>
        </div>

        <div className="flex space-x-2">
          <Button
            size="sm"
            variant="outline"
            className="flex-1 touch-target"
            onClick={() => setSelectedAppointment(appointment)}
          >
            <Eye className="h-4 w-4 mr-1" />
{t('view.details')}
          </Button>
          {appointment.status === 'upcoming' && (
            <Button
              size="sm"
              className="flex-1 touch-target"
              onClick={() => handleStatusUpdate(appointment.id, 'completed')}
              disabled={updateAppointmentMutation.isPending}
            >
              {t('mark.complete')}
            </Button>
          )}
          {user?.canDeletePatients && (
            <Button
              size="sm"
              variant="destructive"
              className="touch-target"
              onClick={() => {
                if (window.confirm(`Are you sure you want to delete this appointment? This action cannot be undone.`)) {
                  deleteAppointmentMutation.mutate(appointment.id);
                }
              }}
              disabled={deleteAppointmentMutation.isPending}
            >
              <Trash2 className="h-4 w-4" />
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );

  if (isLoading) {
    return (
      <div className="p-4 space-y-4 pb-20">
        <div className="animate-pulse space-y-4">
          {[1, 2, 3].map((i) => (
            <div key={i} className="bg-white rounded-xl p-4 shadow-sm border h-32"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="pb-20">
      {/* Header */}
      <div className="p-4 border-b bg-white sticky top-16 z-10">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-xl font-semibold text-neutral-800">{t('appointments.title')}</h1>
          <Button
            size="sm"
            className="touch-target"
            onClick={() => onNavigate('calendar')}
          >
            <Plus className="h-4 w-4 mr-2" />
{t('appointments.book')}
          </Button>
        </div>

        {/* Search and Filter */}
        <div className="space-y-3">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-neutral-400 h-4 w-4" />
            <Input
              placeholder={t('appointments.search.placeholder')}
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 form-input"
            />
          </div>

          <Select value={statusFilter} onValueChange={setStatusFilter}>
            <SelectTrigger>
              <SelectValue placeholder={t('filter.by.status')} />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">{t('appointments.all')}</SelectItem>
              <SelectItem value="upcoming">{t('appointments.upcoming')}</SelectItem>
              <SelectItem value="completed">{t('appointments.completed')}</SelectItem>
              <SelectItem value="cancelled">{t('appointments.cancelled')}</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Appointments List */}
      <div className="p-4">
        <Tabs defaultValue="all" className="space-y-4">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="all">All ({filteredAppointments.length})</TabsTrigger>
            <TabsTrigger value="upcoming">Upcoming ({upcomingAppointments.length})</TabsTrigger>
            <TabsTrigger value="completed">Done ({completedAppointments.length})</TabsTrigger>
            <TabsTrigger value="cancelled">Cancelled ({cancelledAppointments.length})</TabsTrigger>
          </TabsList>

          <TabsContent value="all" className="space-y-3">
            {filteredAppointments.length === 0 ? (
              <Card className="shadow-sm border border-neutral-200">
                <CardContent className="p-8 text-center">
                  <Calendar className="h-16 w-16 text-neutral-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-neutral-800 mb-2">{t('no.data.found')}</h3>
                  <p className="text-neutral-500 mb-4">
{searchTerm ? t('try.different.search') : t('no.appointments.scheduled')}
                  </p>
                  <Button onClick={() => onNavigate('calendar')}>
                    <Plus className="h-4 w-4 mr-2" />
{t('appointments.book')}
                  </Button>
                </CardContent>
              </Card>
            ) : (
              filteredAppointments
                .sort((a: any, b: any) => new Date(b.dateTime).getTime() - new Date(a.dateTime).getTime())
                .map((appointment: any) => (
                  <AppointmentCard key={appointment.id} appointment={appointment} />
                ))
            )}
          </TabsContent>

          <TabsContent value="upcoming" className="space-y-3">
            {upcomingAppointments.length === 0 ? (
              <Card className="shadow-sm border border-neutral-200">
                <CardContent className="p-8 text-center">
                  <Clock className="h-16 w-16 text-neutral-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-neutral-800 mb-2">No upcoming appointments</h3>
                  <p className="text-neutral-500">All caught up!</p>
                </CardContent>
              </Card>
            ) : (
              upcomingAppointments
                .sort((a: any, b: any) => new Date(a.dateTime).getTime() - new Date(b.dateTime).getTime())
                .map((appointment: any) => (
                  <AppointmentCard key={appointment.id} appointment={appointment} />
                ))
            )}
          </TabsContent>

          <TabsContent value="completed" className="space-y-3">
            {completedAppointments.length === 0 ? (
              <Card className="shadow-sm border border-neutral-200">
                <CardContent className="p-8 text-center">
                  <CheckCircle className="h-16 w-16 text-neutral-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-neutral-800 mb-2">No completed appointments</h3>
                  <p className="text-neutral-500">Completed appointments will appear here</p>
                </CardContent>
              </Card>
            ) : (
              completedAppointments
                .sort((a: any, b: any) => new Date(b.dateTime).getTime() - new Date(a.dateTime).getTime())
                .map((appointment: any) => (
                  <AppointmentCard key={appointment.id} appointment={appointment} />
                ))
            )}
          </TabsContent>

          <TabsContent value="cancelled" className="space-y-3">
            {cancelledAppointments.length === 0 ? (
              <Card className="shadow-sm border border-neutral-200">
                <CardContent className="p-8 text-center">
                  <XCircle className="h-16 w-16 text-neutral-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-neutral-800 mb-2">No cancelled appointments</h3>
                  <p className="text-neutral-500">Cancelled appointments will appear here</p>
                </CardContent>
              </Card>
            ) : (
              cancelledAppointments
                .sort((a: any, b: any) => new Date(b.dateTime).getTime() - new Date(a.dateTime).getTime())
                .map((appointment: any) => (
                  <AppointmentCard key={appointment.id} appointment={appointment} />
                ))
            )}
          </TabsContent>
        </Tabs>
      </div>

      {/* Appointment Details Modal */}
      <Dialog open={!!selectedAppointment} onOpenChange={() => setSelectedAppointment(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Appointment Details</DialogTitle>
          </DialogHeader>

          {selectedAppointment && (
            <div className="space-y-4">
              <div className="flex items-center space-x-4">
                <Avatar className="w-16 h-16">
                  <AvatarFallback className="bg-gradient-to-br from-primary to-secondary text-white text-lg">
                    {selectedAppointment.patientName.split(' ').map((n: string) => n[0]).join('').toUpperCase()}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <h3 className="text-lg font-semibold">{selectedAppointment.patientName}</h3>
                  <p className="text-neutral-600">Patient ID: {selectedAppointment.patientId}</p>
                  <p className="text-sm text-neutral-500">
                    {selectedAppointment.patientGender}, {selectedAppointment.patientAge} years
                  </p>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4 text-sm">
                <div>
                  <span className="font-medium text-neutral-700">Appointment ID:</span>
                  <p>{selectedAppointment.appointmentId}</p>
                </div>
                <div>
                  <span className="font-medium text-neutral-700">Status:</span>
                  <span className={`inline-flex items-center space-x-1 px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(selectedAppointment.status)}`}>
                    {getStatusIcon(selectedAppointment.status)}
                    <span className="capitalize">{selectedAppointment.status}</span>
                  </span>
                </div>
                <div>
                  <span className="font-medium text-neutral-700">Date:</span>
                  <p>{format(parseISO(selectedAppointment.dateTime), 'EEEE, MMMM d, yyyy')}</p>
                </div>
                <div>
                  <span className="font-medium text-neutral-700">Time:</span>
                  <p>{format(parseISO(selectedAppointment.dateTime), 'h:mm a')}</p>
                </div>
              </div>

              {selectedAppointment.notes && (
                <div>
                  <span className="font-medium text-neutral-700">Notes:</span>
                  <p className="text-sm mt-1">{selectedAppointment.notes}</p>
                </div>
              )}

              <div className="flex space-x-3 pt-4">
                <Button
                  variant="outline"
                  className="flex-1"
                  onClick={() => setSelectedAppointment(null)}
                >
                  Close
                </Button>
                {selectedAppointment.status === 'upcoming' && (
                  <Button
                    className="flex-1"
                    onClick={() => {
                      handleStatusUpdate(selectedAppointment.id, 'completed');
                      setSelectedAppointment(null);
                    }}
                    disabled={updateAppointmentMutation.isPending}
                  >
                    Mark Complete
                  </Button>
                )}
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/CalendarPage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { format } from "date-fns";
import MobileCalendar from "@/components/mobile/MobileCalendar";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Search } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface CalendarPageProps {
  user: any;
}

export default function CalendarPage({ user }: CalendarPageProps) {
  const [selectedSlot, setSelectedSlot] = useState<{ date: Date; time: string } | null>(null);
  const [selectedPatient, setSelectedPatient] = useState<string>("");
  const [patientType, setPatientType] = useState<"existing" | "new">("existing");
  const [newPatientData, setNewPatientData] = useState({
    name: "",
    age: "",
    location: "",
    gender: "",
    phone: ""
  });
  const [patientSearchQuery, setPatientSearchQuery] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: appointments = [], isLoading: isLoadingAppointments } = useQuery({
    queryKey: ["/api/appointments"],
  });

  const { data: patients = [], isLoading: isLoadingPatients } = useQuery({
    queryKey: ["/api/patients"],
  });

  const createPatientMutation = useMutation({
    mutationFn: async (patientData: any) => {
      const response = await apiRequest("POST", "/api/patients", patientData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/patients"] });
    },
  });

  const createAppointmentMutation = useMutation({
    mutationFn: async (appointmentData: any) => {
      const response = await apiRequest("POST", "/api/appointments", appointmentData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/appointments"] });
      toast({
        title: "Success",
        description: "Appointment booked successfully",
      });
      setSelectedSlot(null);
      setSelectedPatient("");
      setNewPatientData({ name: "", age: "", location: "", gender: "", phone: "" });
      setPatientSearchQuery("");
      setPatientType("existing");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to book appointment",
        variant: "destructive",
      });
    },
  });

  const handleSlotSelect = (date: Date, time: string) => {
    setSelectedSlot({ date, time });
  };

  const handleAppointmentClick = (appointment: any) => {
    // Navigate to appointment details or patient profile
    console.log("Appointment clicked:", appointment);
  };

  const handleBookAppointment = async () => {
    if (!selectedSlot) return;

    let patientId = selectedPatient;

    // If booking for a new patient, create the patient first
    if (patientType === "new") {
      if (!newPatientData.name || !newPatientData.age || !newPatientData.gender) {
        toast({
          title: "Error",
          description: "Please fill in all required fields for the new patient",
          variant: "destructive",
        });
        return;
      }

      try {
        const newPatient = await createPatientMutation.mutateAsync({
          name: newPatientData.name,
          age: parseInt(newPatientData.age),
          gender: newPatientData.gender,
          location: newPatientData.location,
          phone: newPatientData.phone,
          doctorId: user.id,
        });
        patientId = newPatient.id;
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to create new patient",
          variant: "destructive",
        });
        return;
      }
    } else {
      if (!selectedPatient) {
        toast({
          title: "Error",
          description: "Please select a patient",
          variant: "destructive",
        });
        return;
      }
    }

    const [time, period] = selectedSlot.time.split(' ');
    const [hours, minutes] = time.split(':');
    let hour24 = parseInt(hours);

    if (period === 'PM' && hour24 !== 12) {
      hour24 += 12;
    } else if (period === 'AM' && hour24 === 12) {
      hour24 = 0;
    }

    const dateTime = new Date(selectedSlot.date);
    dateTime.setHours(hour24, parseInt(minutes), 0, 0);

    createAppointmentMutation.mutate({
      patientId: patientId,
      dateTime: dateTime.toISOString(),
      status: "upcoming",
      notes: "",
    });
  };

  // Filter patients based on search query
  const filteredPatients = (patients as any[]).filter((patient: any) =>
    patient.name.toLowerCase().includes(patientSearchQuery.toLowerCase()) ||
    patient.patientId.toLowerCase().includes(patientSearchQuery.toLowerCase())
  );

  if (isLoadingAppointments || isLoadingPatients) {
    return (
      <div className="p-4">
        <div className="animate-pulse bg-white rounded-xl shadow-sm border border-neutral-200 h-96"></div>
      </div>
    );
  }

  const enrichedAppointments = (appointments as any[]).map((appointment: any) => {
    const patient = (patients as any[]).find((p: any) => p.id === appointment.patientId);
    return {
      ...appointment,
      patientName: patient?.name || 'Unknown Patient',
      patientId: patient?.patientId || 'N/A',
    };
  });

  return (
    <div className="pb-20">
      <MobileCalendar
        appointments={enrichedAppointments}
        onSlotSelect={handleSlotSelect}
        onAppointmentClick={handleAppointmentClick}
        user={user}
      />

      {/* Appointment Booking Modal */}
      <Dialog open={!!selectedSlot} onOpenChange={() => setSelectedSlot(null)}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Book Appointment</DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <p className="text-sm text-neutral-600 mb-2">Selected Time:</p>
              <p className="font-medium">
                {selectedSlot && format(selectedSlot.date, 'EEEE, MMMM d, yyyy')} at {selectedSlot?.time}
              </p>
            </div>

            <Tabs value={patientType} onValueChange={(value) => setPatientType(value as "existing" | "new")}>
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="existing">Existing Patient</TabsTrigger>
                <TabsTrigger value="new">New Patient</TabsTrigger>
              </TabsList>

              <TabsContent value="existing" className="space-y-3">
                <div>
                  <Label htmlFor="patient-search" className="text-sm font-medium">
                    Search Patient
                  </Label>
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                    <Input
                      id="patient-search"
                      type="text"
                      placeholder="Search by name or ID..."
                      value={patientSearchQuery}
                      onChange={(e) => setPatientSearchQuery(e.target.value)}
                      className="pl-10"
                    />
                  </div>
                </div>

                <div>
                  <Label className="text-sm font-medium">Select Patient</Label>
                  <Select value={selectedPatient} onValueChange={setSelectedPatient}>
                    <SelectTrigger>
                      <SelectValue placeholder="Choose a patient" />
                    </SelectTrigger>
                    <SelectContent>
                      {filteredPatients.map((patient: any) => (
                        <SelectItem key={patient.id} value={patient.id}>
                          {patient.name} ({patient.patientId})
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </TabsContent>

              <TabsContent value="new" className="space-y-3">
                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <Label htmlFor="patient-name" className="text-sm font-medium">
                      Name *
                    </Label>
                    <Input
                      id="patient-name"
                      type="text"
                      value={newPatientData.name}
                      onChange={(e) => setNewPatientData(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Patient name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="patient-age" className="text-sm font-medium">
                      Age *
                    </Label>
                    <Input
                      id="patient-age"
                      type="number"
                      value={newPatientData.age}
                      onChange={(e) => setNewPatientData(prev => ({ ...prev, age: e.target.value }))}
                      placeholder="Age"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="patient-gender" className="text-sm font-medium">
                    Gender *
                  </Label>
                  <Select 
                    value={newPatientData.gender} 
                    onValueChange={(value) => setNewPatientData(prev => ({ ...prev, gender: value }))}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select gender" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="male">Male</SelectItem>
                      <SelectItem value="female">Female</SelectItem>
                      <SelectItem value="other">Other</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="patient-location" className="text-sm font-medium">
                    Location
                  </Label>
                  <Input
                    id="patient-location"
                    type="text"
                    value={newPatientData.location}
                    onChange={(e) => setNewPatientData(prev => ({ ...prev, location: e.target.value }))}
                    placeholder="Patient location"
                  />
                </div>

                <div>
                  <Label htmlFor="patient-phone" className="text-sm font-medium">
                    Phone
                  </Label>
                  <Input
                    id="patient-phone"
                    type="tel"
                    value={newPatientData.phone}
                    onChange={(e) => setNewPatientData(prev => ({ ...prev, phone: e.target.value }))}
                    placeholder="Phone number"
                  />
                </div>
              </TabsContent>
            </Tabs>

            <div className="flex space-x-3 pt-4">
              <Button
                variant="outline"
                className="flex-1"
                onClick={() => setSelectedSlot(null)}
              >
                Cancel
              </Button>
              <Button
                className="flex-1"
                onClick={handleBookAppointment}
                disabled={
                  (patientType === "existing" && !selectedPatient) ||
                  (patientType === "new" && (!newPatientData.name || !newPatientData.age || !newPatientData.gender)) ||
                  createAppointmentMutation.isPending ||
                  createPatientMutation.isPending
                }
              >
                {(createAppointmentMutation.isPending || createPatientMutation.isPending) 
                  ? "Booking..." 
                  : "Book Appointment"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

