import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { CalendarCheck, Users, FileText, Clock, Activity, AlertTriangle, Download } from "lucide-react";
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip } from 'recharts';
import { format } from "date-fns";
import { useLanguage } from "@/contexts/LanguageContext";

interface DashboardPageProps {
  user: any;
  onNavigate: (page: string) => void;
}

export default function DashboardPage({ user, onNavigate }: DashboardPageProps) {
  const { t, translateData } = useLanguage();
  const { data: stats, isLoading: isLoadingStats } = useQuery<any>({
    queryKey: ["/api/stats/dashboard"],
  });

  const { data: appointments = [], isLoading: isLoadingAppointments } = useQuery<any[]>({
    queryKey: ["/api/appointments"],
  });

  const { data: patients = [], isLoading: isLoadingPatients } = useQuery<any[]>({
    queryKey: ["/api/patients"],
  });

  const { data: prescriptions = [], isLoading: isLoadingPrescriptions } = useQuery<any[]>({
    queryKey: ["/api/prescriptions"],
  });

  const { data: medicines = [], isLoading: isLoadingMedicines } = useQuery<any[]>({
    queryKey: ["/api/medicines"],
  });

  const today = new Date();
  const todayString = format(today, 'yyyy-MM-dd');

  const todayAppointments = appointments.filter((apt: any) => {
    const aptDate = format(new Date(apt.dateTime), 'yyyy-MM-dd');
    return aptDate === todayString && apt.status !== 'completed';
  });

  const recentPatients = patients
    .sort((a: any, b: any) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
    .slice(0, 3);

  // Prepare data for symptoms breakdown chart
  const symptomsData = prescriptions.reduce((acc: any, prescription: any) => {
    if (prescription.symptoms) {
      const symptoms = prescription.symptoms.toLowerCase();
      // Extract common symptoms - more comprehensive matching
      const commonSymptoms = ['fever', 'headache', 'cough', 'cold', 'stomach', 'pain', 'anxiety', 'fatigue', 'weakness', 'nausea', 'dizziness', 'insomnia'];

      // Check for multiple symptoms in the text
      commonSymptoms.forEach(symptom => {
        if (symptoms.includes(symptom)) {
          acc[symptom] = (acc[symptom] || 0) + 1;
        }
      });

      // If no common symptoms found, categorize as 'other'
      const hasCommonSymptom = commonSymptoms.some(symptom => symptoms.includes(symptom));
      if (!hasCommonSymptom) {
        acc['other'] = (acc['other'] || 0) + 1;
      }
    }
    return acc;
  }, {});

  const symptomsChartData = Object.entries(symptomsData)
    .map(([name, value]) => ({
      name: name.charAt(0).toUpperCase() + name.slice(1),
      value: value as number,
    }))
    .sort((a, b) => b.value - a.value) // Sort by value descending
    .slice(0, 6); // Top 6 symptoms

  // Prepare data for patient type chart (new vs existing)
  const currentDate = new Date();
  const thirtyDaysAgo = new Date(currentDate.getTime() - (30 * 24 * 60 * 60 * 1000));

  const newPatients = patients.filter((patient: any) => 
    new Date(patient.createdAt) >= thirtyDaysAgo
  ).length;

  const existingPatients = patients.length - newPatients;

  const patientTypeData = [
    { name: 'New Patients (30 days)', value: newPatients },
    { name: 'Existing Patients', value: existingPatients },
  ];

  // Get low stock medicines
  const lowStockMedicines = medicines.filter((medicine: any) => 
    (medicine.currentStock || 0) <= (medicine.lowStockThreshold || 10)
  );

  // Export functions
  const exportToExcel = (data: any[], filename: string) => {
    // Simple CSV export (Excel compatible)
    const headers = ['Medicine Name', 'Code', 'Current Stock', 'Low Stock Threshold', 'Status'];
    const csvContent = [
      headers.join(','),
      ...data.map(medicine => [
        `"${medicine.name}"`,
        medicine.code,
        medicine.currentStock || 0,
        medicine.lowStockThreshold || 10,
        (medicine.currentStock || 0) <= (medicine.lowStockThreshold || 10) ? 'Low Stock' : 'In Stock'
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', `${filename}.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const exportToPDF = (data: any[], filename: string) => {
    // Create a simple HTML table for printing
    const htmlContent = `
      <html>
        <head>
          <title>Low Stock Medicines Report</title>
          <style>
            body { font-family: Arial, sans-serif; margin: 20px; }
            h1 { color: #333; margin-bottom: 20px; }
            table { width: 100%; border-collapse: collapse; margin-top: 20px; }
            th, td { border: 1px solid #ddd; padding: 8px 12px; text-align: left; }
            th { background-color: #f5f5f5; font-weight: bold; }
            .low-stock { color: #dc2626; font-weight: bold; }
            .in-stock { color: #16a34a; }
          </style>
        </head>
        <body>
          <h1>Low Stock Medicines Report</h1>
          <p>Generated on: ${new Date().toLocaleDateString()}</p>
          <table>
            <thead>
              <tr>
                <th>Medicine Name</th>
                <th>Code</th>
                <th>Current Stock</th>
                <th>Low Stock Threshold</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              ${data.map(medicine => `
                <tr>
                  <td>${medicine.name}</td>
                  <td>${medicine.code}</td>
                  <td>${medicine.currentStock || 0}</td>
                  <td>${medicine.lowStockThreshold || 10}</td>
                  <td class="${(medicine.currentStock || 0) <= (medicine.lowStockThreshold || 10) ? 'low-stock' : 'in-stock'}">
                    ${(medicine.currentStock || 0) <= (medicine.lowStockThreshold || 10) ? 'Low Stock' : 'In Stock'}
                  </td>
                </tr>
              `).join('')}
            </tbody>
          </table>
        </body>
      </html>
    `;

    const printWindow = window.open('', '_blank');
    if (printWindow) {
      printWindow.document.write(htmlContent);
      printWindow.document.close();
      printWindow.focus();
      printWindow.print();
    }
  };

  console.log('Prescriptions data:', prescriptions.length);
  console.log('Symptoms chart data:', symptomsChartData);
  console.log('Patient type data:', patientTypeData);

  // Colors for charts
  const SYMPTOM_COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82CA9D'];
  const PATIENT_TYPE_COLORS = ['#00C49F', '#0088FE'];

  const statCards = [
    {
      title: t('dashboard.stats.appointments'),
      value: stats?.todayAppointments || 0,
      icon: CalendarCheck,
      color: "bg-primary bg-opacity-10 text-primary",
      onClick: () => onNavigate('appointments'),
    },
    {
      title: t('dashboard.stats.patients'),
      value: stats?.totalPatients || 0,
      icon: Users,
      color: "bg-secondary bg-opacity-10 text-secondary",
      onClick: () => onNavigate('patients'),
    },
    {
      title: t('dashboard.stats.prescriptions'),
      value: stats?.prescriptionsToday || 0,
      icon: FileText,
      color: "bg-accent bg-opacity-10 text-accent",
      onClick: () => onNavigate('prescriptions'),
    },
    {
      title: t('dashboard.stats.pending'),
      value: stats?.pendingAppointments || 0,
      icon: Clock,
      color: "bg-orange-500 bg-opacity-10 text-orange-500",
      onClick: () => onNavigate('appointments'),
    },
  ];

  if (isLoadingStats || isLoadingAppointments || isLoadingPatients) {
    return (
      <div className="p-4 space-y-4">
        <div className="grid grid-cols-2 gap-4">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="bg-white rounded-xl p-4 shadow-sm border animate-pulse">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-neutral-200 rounded-lg"></div>
                <div className="space-y-2 flex-1">
                  <div className="h-6 bg-neutral-200 rounded w-12"></div>
                  <div className="h-4 bg-neutral-200 rounded w-20"></div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <main className="pb-20 space-y-6">
      {/* Dashboard Stats Cards */}
      <section className="p-4 space-y-4">
        <h2 className="text-xl font-semibold text-neutral-800">Today's Overview</h2>

        <div className="grid grid-cols-2 gap-4">
          {statCards.map((stat, index) => {
            const Icon = stat.icon;

            return (
              <Card 
                key={index}
                className="shadow-sm border border-neutral-200 card-hover cursor-pointer"
                onClick={stat.onClick}
              >
                <CardContent className="p-4">
                  <div className="flex items-center space-x-3">
                    <div className={`w-10 h-10 rounded-lg flex items-center justify-center ${stat.color}`}>
                      <Icon size={20} />
                    </div>
                    <div>
                      <p className="text-2xl font-bold text-neutral-800">{stat.value}</p>
                      <p className="text-sm text-neutral-500">{stat.title}</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      </section>

      {/* Analytics Charts */}
      <section className="p-4 space-y-4">
        <h2 className="text-xl font-semibold text-neutral-800">Analytics Overview</h2>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
          {/* Symptoms Breakdown Chart */}
          <Card className="shadow-sm border border-neutral-200">
            <CardHeader className="pb-3">
              <CardTitle className="flex items-center gap-2 text-lg">
                <Activity className="h-5 w-5" />
                Symptoms Breakdown
              </CardTitle>
            </CardHeader>
            <CardContent>
              {isLoadingPrescriptions ? (
                <div className="h-64 bg-gray-100 rounded animate-pulse" />
              ) : symptomsChartData.length === 0 ? (
                <div className="h-64 flex items-center justify-center">
                  <div className="text-center">
                    <Activity className="h-12 w-12 text-neutral-300 mx-auto mb-3" />
                    <p className="text-neutral-500">No symptoms data available</p>
                    <p className="text-xs text-neutral-400 mt-1">Add prescriptions with symptoms to see chart</p>
                  </div>
                </div>
              ) : (
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                      <Pie
                        data={symptomsChartData}
                        cx="50%"
                        cy="50%"
                        innerRadius={40}
                        outerRadius={80}
                        paddingAngle={5}
                        dataKey="value"
                      >
                        {symptomsChartData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={SYMPTOM_COLORS[index % SYMPTOM_COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip />
                      <Legend />
                    </PieChart>
                  </ResponsiveContainer>
                </div>
              )}
            </CardContent>
          </Card>

          {/* Patient Type Distribution Chart */}
          <Card className="shadow-sm border border-neutral-200">
            <CardHeader className="pb-3">
              <CardTitle className="flex items-center gap-2 text-lg">
                <Users className="h-5 w-5" />
                Patient Type Distribution
              </CardTitle>
            </CardHeader>
            <CardContent>
              {isLoadingPatients ? (
                <div className="h-64 bg-gray-100 rounded animate-pulse" />
              ) : patientTypeData.every(item => item.value === 0) ? (
                <div className="h-64 flex items-center justify-center">
                  <div className="text-center">
                    <Users className="h-12 w-12 text-neutral-300 mx-auto mb-3" />
                    <p className="text-neutral-500">No patient data available</p>
                    <p className="text-xs text-neutral-400 mt-1">Add patients to see distribution chart</p>
                  </div>
                </div>
              ) : (
                <div className="h-64">
                  <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                      <Pie
                        data={patientTypeData}
                        cx="50%"
                        cy="50%"
                        innerRadius={40}
                        outerRadius={80}
                        paddingAngle={5}
                        dataKey="value"
                      >
                        {patientTypeData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={PATIENT_TYPE_COLORS[index % PATIENT_TYPE_COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip />
                      <Legend />
                    </PieChart>
                  </ResponsiveContainer>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </section>

      {/* Today's Appointments */}
      <section className="p-4">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-neutral-800">{t('dashboard.stats.appointments')}</h3>
          <Button
            variant="ghost"
            size="sm"
            className="text-sm text-primary font-medium"
            onClick={() => onNavigate('appointments')}
          >
            {t('view.all')}
          </Button>
        </div>

        <div className="space-y-3">
          {todayAppointments.length === 0 ? (
            <Card className="shadow-sm border border-neutral-200">
              <CardContent className="p-6 text-center">
                <CalendarCheck className="h-12 w-12 text-neutral-300 mx-auto mb-3" />
                <p className="text-neutral-500">No appointments scheduled for today</p>
              </CardContent>
            </Card>
          ) : (
            todayAppointments.slice(0, 3).map((appointment: any) => (
              <Card key={appointment.id} className="shadow-sm border border-neutral-200 card-hover">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <Avatar className="w-10 h-10">
                        <AvatarFallback className="bg-gradient-to-br from-primary to-secondary text-white text-sm">
                          {appointment.patientName?.split(' ').map((n: string) => n[0]).join('').toUpperCase() || 'P'}
                        </AvatarFallback>
                      </Avatar>
                      <div>
                        <h4 className="font-medium text-neutral-800">{appointment.patientName || 'Unknown Patient'}</h4>
                        <p className="text-sm text-neutral-500">ID: {appointment.patientId || 'N/A'}</p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="text-sm font-medium text-neutral-800">
                        {format(new Date(appointment.dateTime), 'h:mm a')}
                      </p>
                      <span className={`inline-block px-2 py-1 text-xs font-medium rounded-full ${
                        appointment.status === 'completed' 
                          ? 'bg-green-100 text-green-600'
                          : 'bg-orange-100 text-orange-600'
                      }`}>
                        {appointment.status === 'completed' ? 'Completed' : 'Upcoming'}
                      </span>
                    </div>
                  </div>
                  <div className="mt-3 flex space-x-2">
                    <Button
                      size="sm"
                      className="flex-1 touch-target"
                      onClick={() => onNavigate('patients')}
                    >
                      View Details
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      className="flex-1 touch-target"
                      onClick={() => onNavigate('prescriptions')}
                    >
                      {appointment.status === 'completed' ? 'View Prescription' : 'Add Prescription'}
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>
      </section>

      {/* Recent Patients */}
      <section className="p-4">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-neutral-800">{t('recent.patients')}</h3>
          <Button
            variant="ghost"
            size="sm"
            className="text-sm text-primary font-medium"
            onClick={() => onNavigate('patients')}
          >
            {t('view.all')}
          </Button>
        </div>

        <div className="grid grid-cols-1 gap-3">
          {recentPatients.length === 0 ? (
            <Card className="shadow-sm border border-neutral-200">
              <CardContent className="p-6 text-center">
                <Users className="h-12 w-12 text-neutral-300 mx-auto mb-3" />
                <p className="text-neutral-500">No patients registered yet</p>
              </CardContent>
            </Card>
          ) : (
            recentPatients.map((patient: any) => (
              <Card key={patient.id} className="shadow-sm border border-neutral-200 card-hover">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <Avatar className="w-10 h-10">
                        <AvatarFallback className="bg-gradient-to-br from-secondary to-accent text-white text-sm">
                          {patient.name?.split(' ').map((n: string) => n[0]).join('').toUpperCase() || 'P'}
                        </AvatarFallback>
                      </Avatar>
                      <div>
                        <h4 className="font-medium text-neutral-800">{patient.name}</h4>
                        <p className="text-sm text-neutral-500">
                          {patient.gender}, {patient.age} ‚Ä¢ {patient.patientId}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="text-xs text-neutral-500">Registered</p>
                      <p className="text-sm font-medium text-neutral-700">
                        {format(new Date(patient.createdAt), 'MMM d')}
                      </p>
                    </div>
                  </div>
                  <div className="mt-3 flex space-x-2">
                    <Button
                      size="sm"
                      className="flex-1 touch-target"
                      onClick={() => onNavigate('patients')}
                    >
                      View Profile
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      className="touch-target px-3"
                      onClick={() => onNavigate('appointments')}
                    >
                      <CalendarCheck className="h-4 w-4" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>
      </section>

      {/* Low Stock Medicines */}
      <section className="p-4">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            <AlertTriangle className="h-5 w-5 text-orange-500" />
            <h3 className="text-lg font-semibold text-neutral-800">Low Stock Medicines</h3>
            {lowStockMedicines.length > 0 && (
              <span className="bg-red-100 text-red-600 text-xs font-medium px-2 py-1 rounded-full">
                {lowStockMedicines.length}
              </span>
            )}
          </div>
          {lowStockMedicines.length > 0 && (
            <div className="flex space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => exportToExcel(lowStockMedicines, 'low-stock-medicines')}
                className="text-sm"
              >
                <Download className="h-4 w-4 mr-1" />
                Excel
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => exportToPDF(lowStockMedicines, 'low-stock-medicines')}
                className="text-sm"
              >
                <Download className="h-4 w-4 mr-1" />
                PDF
              </Button>
            </div>
          )}
        </div>

        <div className="space-y-3">
          {lowStockMedicines.length === 0 ? (
            <Card className="shadow-sm border border-neutral-200">
              <CardContent className="p-6 text-center">
                <Activity className="h-12 w-12 text-green-400 mx-auto mb-3" />
                <p className="text-neutral-500">All medicines are well stocked</p>
                <p className="text-xs text-neutral-400 mt-1">No medicines below stock threshold</p>
              </CardContent>
            </Card>
          ) : (
            lowStockMedicines.map((medicine: any) => (
              <Card key={medicine.id} className="shadow-sm border border-neutral-200 card-hover bg-red-50 border-red-200">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-red-100 rounded-lg flex items-center justify-center">
                        <AlertTriangle className="h-5 w-5 text-red-600" />
                      </div>
                      <div>
                        <h4 className="font-medium text-neutral-800">{medicine.name}</h4>
                        <p className="text-sm text-neutral-500">Code: {medicine.code}</p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-red-600 font-medium">
                        Stock: {medicine.currentStock || 0}
                      </p>
                      <p className="text-xs text-neutral-500">
                        Alert at: {medicine.lowStockThreshold || 10}
                      </p>
                    </div>
                  </div>
                  <div className="mt-3 flex space-x-2">
                    <Button
                      size="sm"
                      className="flex-1 touch-target"
                      onClick={() => onNavigate('medicines')}
                    >
                      Update Stock
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      className="touch-target px-3"
                      onClick={() => onNavigate('medicines')}
                    >
                      View Details
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>
      </section>
    </main>
  );
}

client/src/pages/DashboardPageDesktop.ts.content:
import React, { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Calendar, Users, Clock, CheckCircle, Search, Activity, ChevronLeft, ChevronRight, BarChart3 } from 'lucide-react';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip, BarChart, Bar, XAxis, YAxis, CartesianGrid } from 'recharts';
import type { AuthUser } from '@/lib/auth';
import type { Appointment, Patient } from '@shared/schema';

interface DashboardPageDesktopProps {
  user: AuthUser;
  onNavigate: (page: string) => void;
}

export const DashboardPageDesktop: React.FC<DashboardPageDesktopProps> = ({
  user,
  onNavigate
}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedPeriod, setSelectedPeriod] = useState('month');
  const itemsPerPage = 4;
  const { data: appointments = [] } = useQuery<Appointment[]>({
    queryKey: ["/api/appointments"],
  });

  const { data: patients = [] } = useQuery<Patient[]>({
    queryKey: ["/api/patients"],
  });

  const { data: prescriptions = [], isLoading: isLoadingPrescriptions } = useQuery<any[]>({
    queryKey: ["/api/prescriptions"],
  });

  // Enrich appointments with patient data
  const enrichedAppointments = appointments.map((appointment: any) => {
    const patient = patients.find((p: any) => p.id === appointment.patientId);
    return {
      ...appointment,
      patientName: patient?.name || 'Unknown Patient',
      patientCustomId: patient?.patientId || 'N/A',
    };
  });

  const todayAppointments = enrichedAppointments.filter((apt) => {
    const today = new Date().toDateString();
    return new Date(apt.dateTime).toDateString() === today && apt.status !== 'completed';
  });

  const pendingAppointments = enrichedAppointments.filter((apt) => apt.status === 'upcoming');
  const completedToday = enrichedAppointments.filter((apt) => {
    const today = new Date().toDateString();
    return new Date(apt.dateTime).toDateString() === today && apt.status === 'completed';
  });

  // Prepare data for symptoms breakdown chart
  const symptomsData = prescriptions.reduce((acc: any, prescription: any) => {
    if (prescription.symptoms) {
      const symptoms = prescription.symptoms.toLowerCase();
      // Extract common symptoms - more comprehensive matching
      const commonSymptoms = ['fever', 'headache', 'cough', 'cold', 'stomach', 'pain', 'anxiety', 'fatigue', 'weakness', 'nausea', 'dizziness', 'insomnia'];

      // Check for multiple symptoms in the text
      commonSymptoms.forEach(symptom => {
        if (symptoms.includes(symptom)) {
          acc[symptom] = (acc[symptom] || 0) + 1;
        }
      });

      // If no common symptoms found, categorize as 'other'
      const hasCommonSymptom = commonSymptoms.some(symptom => symptoms.includes(symptom));
      if (!hasCommonSymptom) {
        acc['other'] = (acc['other'] || 0) + 1;
      }
    }
    return acc;
  }, {});

  const symptomsChartData = Object.entries(symptomsData)
    .map(([name, value]) => ({
      name: name.charAt(0).toUpperCase() + name.slice(1),
      value: value as number,
    }))
    .sort((a, b) => b.value - a.value) // Sort by value descending
    .slice(0, 6); // Top 6 symptoms

  // Prepare data for patient type chart (new vs existing)
  const currentDate = new Date();
  const thirtyDaysAgo = new Date(currentDate.getTime() - (30 * 24 * 60 * 60 * 1000));

  const newPatients = patients.filter((patient: any) => 
    new Date(patient.createdAt) >= thirtyDaysAgo
  ).length;

  const existingPatients = patients.length - newPatients;

  const patientTypeData = [
    { name: 'New Patients (30 days)', value: newPatients },
    { name: 'Existing Patients', value: existingPatients },
  ];

  // Colors for charts
  const SYMPTOM_COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82CA9D'];
  const PATIENT_TYPE_COLORS = ['#00C49F', '#0088FE'];

  // Filter appointments for display (excluding completed ones)
  const upcomingAppointments = enrichedAppointments.filter((apt: any) => apt.status !== 'completed');

  // Filter appointments based on search term
  const filteredAppointments = upcomingAppointments.filter((apt: any) => {
    if (!searchTerm) return true;
    const searchLower = searchTerm.toLowerCase();
    return (
      apt.patientName?.toLowerCase().includes(searchLower) ||
      apt.patientCustomId?.toLowerCase().includes(searchLower) ||
      apt.id?.toLowerCase().includes(searchLower)
    );
  });

  // Pagination logic
  const totalPages = Math.ceil(filteredAppointments.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentAppointments = filteredAppointments.slice(startIndex, endIndex);

  // Reset to first page when search changes
  const handleSearchChange = (value: string) => {
    setSearchTerm(value);
    setCurrentPage(1);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  // Prepare medicine usage data based on selected period
  const getMedicineUsageData = () => {
    const now = new Date();
    let startDate = new Date();

    switch (selectedPeriod) {
      case 'week':
        startDate.setDate(now.getDate() - 7);
        break;
      case 'month':
        startDate.setMonth(now.getMonth() - 1);
        break;
      case '3months':
        startDate.setMonth(now.getMonth() - 3);
        break;
      case '6months':
        startDate.setMonth(now.getMonth() - 6);
        break;
      default:
        startDate.setMonth(now.getMonth() - 1);
    }

    // Filter prescriptions by date range
    const filteredPrescriptions = prescriptions.filter((prescription: any) => {
      const prescriptionDate = new Date(prescription.createdAt);
      return prescriptionDate >= startDate && prescriptionDate <= now;
    });

    // Count medicine usage
    const medicineUsage: { [key: string]: number } = {};

    filteredPrescriptions.forEach((prescription: any) => {
      if (prescription.medicines && Array.isArray(prescription.medicines)) {
        prescription.medicines.forEach((medicine: any) => {
          // Now medicine.name should be populated from the backend
          const medicineName = medicine.name;

          if (medicineName && medicineName !== 'Unknown Medicine') {
            medicineUsage[medicineName] = (medicineUsage[medicineName] || 0) + 1;
          }
        });
      }
    });

    // Convert to chart data and sort by usage
    return Object.entries(medicineUsage)
      .map(([name, count]) => ({
        name: name.length > 15 ? name.substring(0, 15) + '...' : name,
        fullName: name,
        count: count as number,
      }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 10); // Top 10 medicines
  };

  const medicineUsageData = getMedicineUsageData();

  const periodOptions = [
    { value: 'week', label: '1 Week' },
    { value: 'month', label: '1 Month' },
    { value: '3months', label: '3 Months' },
    { value: '6months', label: '6 Months' },
  ];

  const stats = [
    {
      title: "TODAY'S APPOINTMENTS",
      value: todayAppointments.length,
      icon: Calendar,
      color: "bg-blue-500",
      bgClass: "bg-blue-50"
    },
    {
      title: "TOTAL PATIENTS", 
      value: patients.length,
      icon: Users,
      color: "bg-green-500",
      bgClass: "bg-green-50"
    },
    {
      title: "PENDING",
      value: pendingAppointments.length,
      icon: Clock,
      color: "bg-orange-500", 
      bgClass: "bg-orange-50"
    },
    {
      title: "COMPLETED TODAY",
      value: completedToday.length,
      icon: CheckCircle,
      color: "bg-green-500",
      bgClass: "bg-green-50"
    }
  ];

  return (
    <div className="p-6 max-w-7xl mx-auto space-y-6">
      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <Card key={index} className={`${stat.bgClass} border-0 shadow-sm`}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-xs font-medium text-gray-600 tracking-wide uppercase">
                      {stat.title}
                    </p>
                    <p className="text-3xl font-bold text-gray-900 mt-2">
                      {stat.value}
                    </p>
                  </div>
                  <div className={`${stat.color} p-3 rounded-full`}>
                    <Icon className="h-6 w-6 text-white" />
                  </div>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Appointments Table */}
      <Card className="shadow-sm">
        <CardHeader className="border-b border-gray-100">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Calendar className="h-5 w-5 text-primary" />
              <CardTitle className="text-lg font-semibold">Upcoming Appointments</CardTitle>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-500">
                {new Date().toLocaleDateString('en-US', { 
                  month: 'numeric', 
                  day: 'numeric', 
                  year: 'numeric' 
                })}
              </span>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input 
                  placeholder="Search patient..." 
                  className="pl-10 w-64"
                  value={searchTerm}
                  onChange={(e) => handleSearchChange(e.target.value)}
                />
              </div>
            </div>
          </div>
        </CardHeader>
        <CardContent className="p-0">
          {filteredAppointments.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-12">
              <Calendar className="h-12 w-12 text-gray-300 mb-4" />
              <p className="text-gray-500 text-center">
                {searchTerm ? `No appointments found for "${searchTerm}"` : 'No upcoming appointments'}
              </p>
            </div>
          ) : (
            <>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Appointment ID
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Date & Time
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Patient Name
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Patient ID
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Status
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {currentAppointments.map((appointment) => (
                      <tr key={appointment.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {appointment.appointmentId}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {new Date(appointment.dateTime).toLocaleString()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {appointment.patientName}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {appointment.patientCustomId}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                            appointment.status === 'completed' 
                              ? 'bg-green-100 text-green-800'
                              : appointment.status === 'pending'
                              ? 'bg-yellow-100 text-yellow-800'
                              : 'bg-gray-100 text-gray-800'
                            }`}>
                            {appointment.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          <Button 
                            variant="ghost" 
                            size="sm" 
                            className="text-primary hover:text-primary-dark"
                            onClick={() => onNavigate('appointments')}
                          >
                            View Details
                          </Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Pagination Controls */}
              {totalPages > 1 && (
                <div className="flex items-center justify-between px-6 py-4 border-t border-gray-200">
                  <div className="flex items-center space-x-2 text-sm text-gray-500">
                    <span>
                      Showing {startIndex + 1} to {Math.min(endIndex, filteredAppointments.length)} of {filteredAppointments.length} appointments
                    </span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={handlePrevPage}
                      disabled={currentPage === 1}
                      className="flex items-center space-x-1"
                    >
                      <ChevronLeft className="h-4 w-4" />
                      <span>Previous</span>
                    </Button>

                    <div className="flex items-center space-x-1">
                      {Array.from({ length: totalPages }, (_, i) => i + 1).map((pageNum) => (
                        <Button
                          key={pageNum}
                          variant={pageNum === currentPage ? "default" : "outline"}
                          size="sm"
                          onClick={() => setCurrentPage(pageNum)}
                          className="w-8 h-8 p-0"
                        >
                          {pageNum}
                        </Button>
                      ))}
                    </div>

                    <Button
                      variant="outline"
                      size="sm"
                      onClick={handleNextPage}
                      disabled={currentPage === totalPages}
                      className="flex items-center space-x-1"
                    >
                      <span>Next</span>
                      <ChevronRight className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              )}
            </>
          )}
        </CardContent>
      </Card>

      {/* Analytics Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Symptoms Breakdown Chart */}
        <Card className="shadow-sm">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2 text-lg">
              <Activity className="h-5 w-5" />
              Symptoms Breakdown
            </CardTitle>
          </CardHeader>
          <CardContent>
            {isLoadingPrescriptions ? (
              <div className="h-64 bg-gray-100 rounded animate-pulse" />
            ) : symptomsChartData.length === 0 ? (
              <div className="h-64 flex items-center justify-center">
                <div className="text-center">
                  <Activity className="h-12 w-12 text-gray-300 mx-auto mb-3" />
                  <p className="text-gray-500">No symptoms data available</p>
                  <p className="text-xs text-gray-400 mt-1">Add prescriptions with symptoms to see chart</p>
                </div>
              </div>
            ) : (
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={symptomsChartData}
                      cx="50%"
                      cy="50%"
                      innerRadius={40}
                      outerRadius={80}
                      paddingAngle={5}
                      dataKey="value"
                    >
                      {symptomsChartData.map((entry: any, index: number) => (
                        <Cell key={`cell-${index}`} fill={SYMPTOM_COLORS[index % SYMPTOM_COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Patient Type Distribution Chart */}
        <Card className="shadow-sm">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2 text-lg">
              <Users className="h-5 w-5" />
              Patient Type Distribution
            </CardTitle>
          </CardHeader>
          <CardContent>
            {patients.length === 0 ? (
              <div className="h-64 flex items-center justify-center">
                <div className="text-center">
                  <Users className="h-12 w-12 text-gray-300 mx-auto mb-3" />
                  <p className="text-gray-500">No patient data available</p>
                  <p className="text-xs text-gray-400 mt-1">Add patients to see distribution chart</p>
                </div>
              </div>
            ) : (
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={patientTypeData}
                      cx="50%"
                      cy="50%"
                      innerRadius={40}
                      outerRadius={80}
                      paddingAngle={5}
                      dataKey="value"
                    >
                      {patientTypeData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={PATIENT_TYPE_COLORS[index % PATIENT_TYPE_COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Bottom Analytics Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Medicine Usage Volume Chart (Circular) */}
        <Card className="shadow-sm">
          <CardHeader className="border-b border-gray-100">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <BarChart3 className="h-5 w-5 text-primary" />
                <CardTitle className="text-lg font-semibold">Medicine Usage Volume</CardTitle>
              </div>
              <div className="flex items-center space-x-2">
                {periodOptions.map((option) => (
                  <Button
                    key={option.value}
                    variant={selectedPeriod === option.value ? "default" : "outline"}
                    size="sm"
                    onClick={() => setSelectedPeriod(option.value)}
                    className="text-xs"
                  >
                    {option.label}
                  </Button>
                ))}
              </div>
            </div>
          </CardHeader>
          <CardContent className="p-6">
            {isLoadingPrescriptions ? (
              <div className="flex items-center justify-center h-64">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
              </div>
            ) : medicineUsageData.length === 0 ? (
              <div className="flex flex-col items-center justify-center h-64">
                <BarChart3 className="h-12 w-12 text-gray-300 mb-4" />
                <p className="text-gray-500 text-center">
                  No medicine usage data available for the selected period
                </p>
              </div>
            ) : (
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={medicineUsageData.slice(0, 6)}
                      cx="50%"
                      cy="50%"
                      innerRadius={40}
                      outerRadius={80}
                      paddingAngle={5}
                      dataKey="count"
                    >
                      {medicineUsageData.slice(0, 6).map((entry: any, index: number) => (
                        <Cell key={`cell-${index}`} fill={SYMPTOM_COLORS[index % SYMPTOM_COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip 
                      formatter={(value: any, name: any, props: any) => [
                        `${value} times`, 
                        'Used'
                      ]}
                      labelFormatter={(label: any, payload: any) => {
                        const data = payload?.[0]?.payload;
                        return data ? data.fullName : label;
                      }}
                    />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            )}
            {medicineUsageData.length > 0 && (
              <div className="mt-4 text-sm text-gray-500 text-center">
                Top 6 medicines in the last {periodOptions.find(p => p.value === selectedPeriod)?.label.toLowerCase()}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Appointments vs Prescriptions Chart */}
        <Card className="shadow-sm">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2 text-lg">
              <Clock className="h-5 w-5" />
              Appointments vs Prescriptions
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={[
                      { name: `Total Appointments (${appointments.length})`, value: appointments.length },
                      { name: `Total Prescriptions (${prescriptions.length})`, value: prescriptions.length }
                    ]}
                    cx="50%"
                    cy="50%"
                    innerRadius={40}
                    outerRadius={80}
                    paddingAngle={5}
                    dataKey="value"
                  >
                    <Cell fill="#0088FE" />
                    <Cell fill="#00C49F" />
