    if (name.length >= 3) {
      let baseCode = name.substring(0, 3).toUpperCase();
      let uniqueCode = baseCode;
      let counter = 1;

      // Check if code already exists and generate unique one
      while (medicines.find(med => med.code === uniqueCode)) {
