    const newMedicine = {
      medicineId: matchingMedicine?.id || "",
      dosage: suggestion.dosage || suggestion.power || "",
      frequency: suggestion.frequency || "",
      duration: suggestion.duration || "",
      instructions: suggestion.instructions || ""
    };

