client/src/components/auth/LoginForm.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Eye, EyeOff, AlertCircle } from "lucide-react";
import { MedicalLogo } from "@/components/ui/MedicalLogo";
import { useHapticFeedback } from "@/hooks/use-pwa";
// Import background image directly
import backgroundImageUrl from "@assets/background_1753640941516.jpg";

// Debug: log the image URL to console
console.log('Background image URL:', backgroundImageUrl);

interface LoginFormProps {
  onLogin: (username: string, password: string) => Promise<boolean>;
  isLoading: boolean;
  error?: string;
}

export default function LoginForm({ onLogin, isLoading, error }: LoginFormProps) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const { lightTap } = useHapticFeedback();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    lightTap();

    if (!username.trim() || !password.trim()) {
      return;
    }

    await onLogin(username.trim(), password);
  };

  const demoAccounts = [
    { username: "admin", password: "admin123", role: "Administrator" },
    { username: "doctor", password: "doctor123", role: "Doctor" },
    { username: "ranajit", password: "ranajit123", role: "Dr. Ranajit" },
  ];

  const fillDemo = (username: string, password: string) => {
    lightTap();
    setUsername(username);
    setPassword(password);
  };

  return (
    // Mobile-first responsive login layout
    <div className="login-background min-h-screen flex items-center justify-center md:justify-end p-4 md:pr-16 relative">
      {/* Background image with opacity */}
      <div 
        className="absolute inset-0 bg-blue-500"
        style={{
          backgroundImage: `url(${backgroundImageUrl})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          opacity: 0.6
        }}
      ></div>
      {/* Overlay for better contrast - Updated styling */}
      <div className="absolute inset-0 from-white/60 via-primary/20 to-white/40 bg-[#2a2c3700] z-10"></div>
      <Card className="w-full max-w-md mx-auto md:mx-0 relative z-20 bg-white/95 backdrop-blur-sm shadow-2xl border-0">
        <CardHeader className="text-center pb-4">
          <div className="flex items-center justify-center mb-4">
            <MedicalLogo showBackground={true} size={64} className="mr-4" />
            <div className="text-left">
              <h1 className="text-2xl font-bold">
                <span className="text-blue-600">My Homeo</span>{" "}
                <span className="text-teal-600">Health</span>
              </h1>
              <p className="text-gray-500 text-sm">Doctor Dashboard</p>
            </div>
          </div>
          <p className="text-neutral-600 mt-4">
            Sign in to access your dashboard
          </p>
        </CardHeader>

        <CardContent className="space-y-6">
          {error && (
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="username">Username</Label>
              <Input
                id="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter your username"
                className="form-input touch-target"
                required
                autoComplete="username"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="password">Password</Label>
              <div className="relative">
                <Input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  className="form-input touch-target pr-10"
                  required
                  autoComplete="current-password"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4 text-neutral-400" />
                  ) : (
                    <Eye className="h-4 w-4 text-neutral-400" />
                  )}
                </Button>
              </div>
            </div>

            <Button
              type="submit"
              className="w-full touch-target"
              disabled={isLoading || !username.trim() || !password.trim()}
            >
              {isLoading ? "Signing in..." : "Sign In"}
            </Button>
          </form>

          <div className="space-y-3">
            <div className="relative">
              <div className="absolute inset-0 flex items-center">
                <span className="w-full border-t border-neutral-200" />
              </div>
              <div className="relative flex justify-center text-xs uppercase">
                <span className="bg-white px-2 text-neutral-500">Demo Accounts</span>
              </div>
            </div>

            <div className="grid grid-cols-1 gap-2">
              {demoAccounts.map((account) => (
                <Button
                  key={account.username}
                  variant="outline"
                  size="sm"
                  className="justify-start touch-target min-h-[48px] text-left p-3"
                  onClick={() => fillDemo(account.username, account.password)}
                  type="button"
                >
                  <div className="text-left w-full">
                    <div className="font-medium text-sm">{account.role}</div>
                    <div className="text-xs text-neutral-500">
                      {account.username} / {account.password}
                    </div>
                  </div>
                </Button>
              ))}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/layout/DesktopHead.content:
import React, { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { Calendar, Users, Pill, FileText, BarChart3, User, LogOut, ChevronDown, Settings, MessageCircle, Languages } from 'lucide-react';
import { MedicalLogo } from "@/components/ui/MedicalLogo";
import ProfileModal from "@/components/ProfileModal";
import WhatsAppModal from "@/components/WhatsAppModal";
import { useLanguage } from "@/contexts/LanguageContext";
import type { AuthUser } from '@/lib/auth';

interface DesktopHeaderProps {
  user: AuthUser;
  currentPage: string;
  onNavigate: (page: string) => void;
  onLogout: () => void;
}

export const DesktopHeader: React.FC<DesktopHeaderProps> = ({
  user,
  currentPage,
  onNavigate,
  onLogout
}) => {
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [showWhatsAppModal, setShowWhatsAppModal] = useState(false);
  const { t, language, setLanguage } = useLanguage();
  const isAdmin = user.role === 'admin';
  const isDoctor = user.role === 'doctor';

  const navigationItems = [
    { key: 'dashboard', label: t('nav.dashboard'), icon: BarChart3, show: true },
    { key: 'patients', label: t('nav.patients'), icon: Users, show: isAdmin || isDoctor },
    { key: 'medicines', label: t('nav.medicines'), icon: Pill, show: isAdmin || isDoctor },
    { key: 'appointments', label: t('nav.appointments'), icon: Calendar, show: isAdmin || isDoctor },
    { key: 'prescriptions', label: t('nav.prescriptions'), icon: FileText, show: isAdmin || isDoctor },
  ];

  return (
    <header className="bg-white border-b border-gray-200 px-6 py-4">
      <div className="flex items-center justify-between max-w-7xl mx-auto">
        {/* Logo and Brand */}
        <div className="flex items-center space-x-3">
          <MedicalLogo showBackground={true} size={48} />
          <div>
            <h1 className="text-xl font-bold">
              <span className="text-blue-600">My Homeo</span>{" "}
              <span className="text-teal-600">Health</span>
            </h1>
            <p className="text-sm text-gray-500">{isAdmin ? t('dashboard.admin') : t('dashboard.doctor')}</p>
          </div>
        </div>

        {/* Navigation */}
        <nav className="flex items-center space-x-1">
          {navigationItems.filter(item => item.show).map((item) => {
            const Icon = item.icon;
            const isActive = currentPage === item.key;

            return (
              <Button
                key={item.key}
                variant={isActive ? "default" : "ghost"}
                className={`flex items-center space-x-2 px-4 py-2 ${
                  isActive 
                    ? 'bg-primary text-white' 
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                }`}
                onClick={() => onNavigate(item.key)}
              >
                <Icon className="h-4 w-4" />
                <span>{item.label}</span>
              </Button>
            );
          })}
        </nav>

        {/* User Profile */}
        <div className="flex items-center space-x-4">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                className="flex items-center space-x-3 text-left p-2 hover:bg-gray-100"
              >
                <Avatar className="h-8 w-8 bg-primary">
                  <AvatarFallback className="text-white text-sm font-medium">
                    {user.username.slice(0, 2).toUpperCase()}
                  </AvatarFallback>
                </Avatar>
                <div className="text-right">
                  <p className="text-sm font-medium text-gray-900">
                    {user.name || `Dr. ${user.username}`}
                  </p>
                  <p className="text-xs text-gray-500 capitalize">
                    {user.role}
                  </p>
                </div>
                <ChevronDown className="h-4 w-4 text-gray-400" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-48">
              <DropdownMenuItem onClick={() => setShowProfileModal(true)}>
                <User className="h-4 w-4 mr-2" />
                {t('nav.profile')}
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => setLanguage(language === 'en' ? 'bn' : 'en')}>
                <Languages className="h-4 w-4 mr-2" />
                {language === 'en' ? '‡¶¨‡¶æ‡¶Ç‡¶≤‡¶æ' : 'English'}
              </DropdownMenuItem>
              {user?.role === 'doctor' && (
                <DropdownMenuItem onClick={() => onNavigate('doctor/settings')}>
                  <Settings className="h-4 w-4 mr-2" />
                  Set Availability
                </DropdownMenuItem>
              )}
              {user?.role === 'doctor' && (
                <DropdownMenuItem onClick={() => setShowWhatsAppModal(true)}>
                  <MessageCircle className="h-4 w-4 mr-2" />
                  WhatsApp Booking
                </DropdownMenuItem>
              )}
              {user?.role === 'admin' && (
                <DropdownMenuItem onClick={() => {
                  onNavigate('dashboard');
                  setTimeout(() => {
                    const adminTab = document.querySelector('[data-value="admins"]') as HTMLElement;
                    if (adminTab) {
                      adminTab.click();
                    }
                  }, 200);
                }}>
                  <Users className="h-4 w-4 mr-2" />
                  Manage Admin Users
                </DropdownMenuItem>
              )}
              <DropdownMenuItem onClick={onLogout}>
                <LogOut className="h-4 w-4 mr-2" />
                {t('nav.logout')}
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>

        <ProfileModal 
          isOpen={showProfileModal}
          onClose={() => setShowProfileModal(false)}
          onLogout={onLogout}
          user={user}
        />
        <WhatsAppModal
          isOpen={showWhatsAppModal}
          onClose={() => setShowWhatsAppModal(false)}
          user={user}
        />
      </div>
    </header>
  );
};

client/src/components/mobile/BottomNavig.content:
import { Calendar, Users, BarChart3, FileText, Pill } from "lucide-react";
import { cn } from "@/lib/utils";
import { useHapticFeedback } from "@/hooks/use-pwa";
import { useLanguage } from "@/contexts/LanguageContext";

interface BottomNavigationProps {
  currentPage: string;
  onNavigate: (page: string) => void;
  userRole: string;
}

export default function BottomNavigation({ currentPage, onNavigate, userRole }: BottomNavigationProps) {
  const { lightTap } = useHapticFeedback();
  const { t } = useLanguage();

  const handleNavigate = (page: string) => {
    lightTap();
    onNavigate(page);
  };

  const navigationItems = [
    { id: 'calendar', label: t('nav.calendar'), icon: Calendar, roles: ['doctor'] },
    { id: 'patients', label: t('nav.patients'), icon: Users, roles: ['admin', 'doctor'] },
    { id: 'dashboard', label: t('nav.dashboard'), icon: BarChart3, roles: ['admin', 'doctor'] },
    { id: 'prescriptions', label: t('nav.prescriptions'), icon: FileText, roles: ['admin', 'doctor'] },
    { id: 'medicines', label: t('nav.medicines'), icon: Pill, roles: ['admin', 'doctor'] },
  ];

  const filteredItems = navigationItems.filter(item => 
    item.roles.includes(userRole || 'patient')
  );

  return (
    <nav className="mobile-nav">
      <div className={`grid h-16 ${filteredItems.length === 5 ? 'grid-cols-5' : 'grid-cols-4'}`}>
        {filteredItems.map((item) => {
          const Icon = item.icon;
          const isActive = currentPage === item.id;

          return (
            <button
              key={item.id}
              className={cn(
                "flex flex-col items-center justify-center transition-smooth touch-target",
                isActive 
                  ? "text-primary" 
                  : "text-neutral-400 hover:text-neutral-600"
              )}
              onClick={() => handleNavigate(item.id)}
            >
              <Icon className="w-5 h-5 mb-1" />
              <span className="text-xs font-medium">{item.label}</span>
            </button>
          );
        })}
      </div>
    </nav>
  );
}

